INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_1", /*message*/"　넋 놓고 저녁노을을 바라보던 당신의 발치에 웬 귤이 굴러왔다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_2", /*message*/"　치카와 함께 귤을 주운 다음 날, 도움에 대한 보답으로 치카는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_3", /*message*/"　치카에게 초대받은 당신은 치카의 방에서 어떤 물건을 발견한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_4", /*message*/"　작사 노트를 통해 치카의 고뇌와 Aqours에 대한 사랑이 생생하게 전해졌다. 감동한 당신에게 치카는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_5", /*message*/"　치카의 가사에는 사랑이 넘치고 있었다. 당신의 응원으로 작사 의욕이 솟아난 치카. 라이브 당일, 과연 곡은 완성됐을까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_6", /*message*/"　치카와 영상 통화를 하며 근황 이야기를 나누는 당신. 대화를 나누던 중, 치카는 어떤 일에 관심을 보이기 시작하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_7", /*message*/"　당신과 근황 이야기를 나누다가 솔로 라이브에 관심을 보인 치카는 바로 솔로 라이브를 기획하고 리코와 요우에게 그 사실을 밝힌다. 두 사람의 반응은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_8", /*message*/"　솔로 라이브를 하겠다고 밝힌 치카에게, 멤버들은 놀라면서도 응원을 보냈다. 치카는 점점 더 솔로 라이브에 열정을 쏟지만, 정작 중요한 것을 잊고 있었다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_1", /*message*/"　피아노 콘서트 포스터를 보고 관심이 생겨 콘서트홀을 방문한 당신. 티켓을 사서 들어간 공연장에서 생각지도 못한 인물과 마주친다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_2", /*message*/"　콘서트에 출연한 피아니스트는 놀랍게도 리코가 과거에 피아노를 배웠던 사람이었다. 리코는 과거를 회상하면서도 뭔가 심각한 듯 생각에 잠겨 있는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_3", /*message*/"　누마즈에서 리코와 재회한 당신은 리코에게 선생님과 어떤 이야기를 나누었는지 묻는다. 리코가 이야기한 내용은 우치우라 생활에 관한 것, 그리고….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_4", /*message*/"　Aqours를 위해 좋은 곡을 만들고자 매일 고민하고 시행착오를 반복하던 리코. 좀처럼 마음에 드는 성과를 내지 못하는 그녀에게 당신은 한 가지 부탁을 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_5", /*message*/"　리코의 피아노를 듣고 싶어서 함께 찾아간 우라 여고 음악실에서 당신이 리퀘스트한 곡은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_6", /*message*/"　당신 덕분에 리코는 긍정적으로 작곡에 임할 수 있게 되었다. 완전히 컨디션이 돌아온 리코의 모습을 본 당신도 마음을 놓을 수 있었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_7", /*message*/"　아홉 명으로 구성된 그룹인 Aqours의 곡을 만드는 리코와 니지가사키 멤버 아홉 명 각각의 곡을 만드는 당신. 상황은 달라도 같은 작곡 담당인 두 사람 사이에는 통하는 무언가가 있었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_8", /*message*/"　당신의 초대를 받아 니지가사키 학원을 찾은 리코. 동호회 멤버들의 따뜻한 환영을 받은 리코는 연습을 견학하기로 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_1", /*message*/"　아와시마를 처음 방문한 당신. 무엇을 보고 어떤 사람을 만나게 될까? 기대로 부푼 가슴을 안고 향한 곳은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_2", /*message*/"　카난의 다이빙 이야기를 듣고 매력을 느낀 당신. 카난의 도움을 받아 바다에 잠수해 보기로 한다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_3", /*message*/"　카난과 함께 우치우라의 바다에 잠수한 당신. 이 아름다움을 많은 사람에게 알리고 싶은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_4", /*message*/"　당신과 카난, 그리고 마리까지 더해 우치우라 바다의 장점을 널리 알리기 위한 작전 회의 개시!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_5", /*message*/"　우치우라의 다이빙 스폿을 홍보하기 위해 팔을 걷어붙이고 나선 마리. 우선은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_6", /*message*/"　PR 영상이 호평을 받아 활기가 도는 다이빙 숍. 당신과 카난은 만족했지만, 현실은 그렇게 녹록하지 않은 듯 한데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_7", /*message*/"　PR 영상 제2탄 제작을 앞두고 회의가 열렸다. 다양한 의견이 오갔지만 좀처럼 진전이 없던 가운데, 생각지도 못한 부분에서 힌트를 얻는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_8", /*message*/"　다 함께 솔로 라이브 동영상을 만들게 되었다. 하지만 라이브를 하려면 곡이 필요한데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_1", /*message*/"　당신은 학교 미화 활동의 일환으로 쓰레기를 줍던 다이아를 만났다. 학생회장으로서 다른 학생들도 신경 쓰는 다이아. 스쿨 아이돌과는 다른 그 모습이 신선했다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_2", /*message*/"　우라 여고 부실에서 루비와 함께 잡지를 읽던 당신. 그때 신비한 음색이 들려왔다. 그 소리는 교내에 있는 일본식 방에서 들려오는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_3", /*message*/"　다이아가 있던 곳은 꽃꽂이부 부실이었다. 꽃꽂이부에서 실력을 인정받는 다이아의 권유로 당신과 루비는 꽃꽂이에 도전하게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_4", /*message*/"　하나마루와 약속이 있다는 루비와 헤어진 뒤, 당신과 다이아는 부실로 돌아왔다. 루비와 함께 잡지를 봤다고 이야기하던 당신은 문득 다이아도 아이돌 잡지를 보는지 궁금해졌는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_5", /*message*/"　저녁 무렵, 집에 가는 길에 당신은 다이아에게 질문했다. 할 일을 잔뜩 끌어안고 있는 게 힘들지 않냐고. 그러자 다이아는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_6", /*message*/"　어느 휴일, 당신은 아이와 함께 하라주쿠에 놀러 왔다. 어디에 갈지 상의하던 중, 생각지도 못한 사람을 발견한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_7", /*message*/"　다이아, 아이, 당신은 셋이서 하라주쿠를 구경하러 갔다. 사람들로 붐비는 거리를 헤치며 세 사람이 향한 곳은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_8", /*message*/"　하라주쿠를 돌아다닌 다이아는 조금 피곤해 보인다. 그러자 아이가 다른 곳에서 놀자고 제안하는데, 그곳은 바로….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_1", /*message*/"　누마즈에 놀러 온 당신. 역을 나섰을 때 우연히 요우를 만나서 함께 누마즈 거리를 돌아다니게 되었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_2", /*message*/"　가장 먼저 찾아간 곳은 역 앞 쇼핑센터. 이곳에 있는 게임 센터에서 인형 뽑기에 도전!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_3", /*message*/"　케이크를 먹으면서 다음은 어디로 갈지 상의하는 당신과 요우. 수족관에 갈까, 성터에 갈까? 즐거운 하루는 이제부터 시작이다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_4", /*message*/"　커다란 수문에 온 당신과 요우. 전망대에서 보이는 절경에 무척 신이 난 당신. 그런데 왠지 요우가 조용한데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_5", /*message*/"　해가 저물어 도쿄로 돌아갈 시간이 되었다. 다음에 또 놀자는 약속과 함께, 당신은 개찰구를 통과했다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_6", /*message*/"　요우와 함께 누마즈에서 즐겁게 지냈던 당신은, 다음에 요우에게 오다이바를 안내하고 싶다고 생각한다. 이때 갑자기 전화가 걸려오는데, 그 상대는 바로….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_7", /*message*/"　오다이바에서 요우와 만나 놀기로 약속한 당일, 약속 장소에는 당신 말고도 요우를 기다리는 또 다른 사람이 있었다. 오늘은 과연 어떤 하루가 될까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_8", /*message*/"　세 사람은 관람차에서 내린 후 쇼핑몰을 찾았다. 쇼핑몰 광장에서는 다양한 이벤트가 열린다고 한다. 언젠가 여기서 라이브를 하는 날이 올까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_1", /*message*/"　길가에서 기묘한 물체를 주운 당신. 파출소에 가져가야겠다고 생각한 바로 그때, 요시코가 말을 걸어왔다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_2", /*message*/"　열쇠고리를 고쳐준 당신에게 요시코가 감사의 뜻으로 타락천사 요하네의 측근 지위를 하사했다. 이로써 새로운 리틀 데몬이 탄생했다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_3", /*message*/"　요시코가 당신에게 &apos;리틀 데몬 증원 계획&apos;이라는 명목으로 동영상 촬영에 협조할 것을 요구했다. 촬영하면서 시도하고 싶은 게 있다는 요시코. 대체 뭘 하려는 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_4", /*message*/"　동영상 촬영 당일. 기대와 긴장감으로 안절부절못하는 당신과 대조적으로 요시코는 시무룩해 보인다. 아무래도 동영상에서 플레이를 보여줄 게임에 불만이 있는 것 같다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_5", /*message*/"　생방송이 시작되어 요하네 모드가 된 요시코와 함께 게임을 시작한 당신. 어떤 방송이 될지 두근거린 것도 잠시, 요시코의 비명이 울려 퍼진다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_6", /*message*/"　영상 촬영을 위해 요시코네 집으로 가려던 당신에게, 요시코 본인으로부터 연락이 온다. &quot;긴급 사태!&quot;라며 다급해하는 요시코. 과연 무슨 일이 생긴 것일까?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_7", /*message*/"　오프라인 모임에 참가하기로 한 요시코. 하지만 당일에 무슨 옷을 입고, 무슨 이야기를 할지 걱정이 끊이지 않는 것 같다. 당신도 아이디어를 내면서 고민을 하나씩 해결해 간다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_8", /*message*/"　오프라인 모임 당일. 당신은 긴장을 떨치지 못하는 요시코를 데리고 모임 장소로 향한다. 과연 실수 없이 무사히 오프라인 모임을 마칠 수 있을까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_1", /*message*/"　산책과 휴식을 겸해 도서관을 방문한 당신. 도서관 안을 돌아다니다 어디서 귀에 익은 목소리가 들려오는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_2", /*message*/"　하나마루가 책을 빌리는 동안, 당신과 루비는 도서관 게시판을 구경했다. 게시판에 붙어 있는 다양한 공지 중 특별히 눈길을 끄는 것이 있었는데, 그것은 바로….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_3", /*message*/"　도서관 문집에 관심이 있지만 응모할 결심이 서지 않는 하나마루. 가능성을 놓치지 않길 바라는 당신과 루비는 하나마루가 용기를 낼 수 있게 돕는다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_4", /*message*/"　문집에 응모하기로 결심한 하나마루. 당신과 루비는 하나마루를 응원하지만, 집필을 위해서는 &apos;어떤 문제&apos;를 극복해야 한다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_5", /*message*/"　도서관 문집 발행일. 당신과 하나마루, 루비는 도서관을 찾았다. 특설 코너에 하나마루가 응모한 문집이 전시되어 있었는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_6", /*message*/"　방에서 하나마루와 전화하는 당신. 도서관 단편집에 투고한 이후, 하나마루는 소설을 쓰는 일이 늘었다고 한다. 하나마루는 글을 쓰는 즐거움을 느끼고 있었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_7", /*message*/"　시즈쿠가 뭔가 고민이 있는 것 같다. 자초지종을 묻는 당신에 대한 시즈쿠의 대답은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_8", /*message*/"　하나마루를 만나기 위해 누마즈를 방문한 당신과 시즈쿠. 그런데 마중 나온다던 하나마루가 보이지 않는다. 무슨 일이 생긴 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_1", /*message*/"　도쿄 거리를 걷던 당신은 길을 물어보는 외국인 관광객에게 대답을 못 하고 당황하고 만다. 이때 뜻밖의 인물이 구원의 손길을 내미는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_2", /*message*/"　마리의 도쿄 관광에 동행하게 된 당신. 목적지를 물어봐도 마리는 웃으며 말을 흐리기만 한다. 마침내 도착한 곳은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_3", /*message*/"　마리의 마이 페이스에 따라가는 것도 벅찬 당신. 심지어 마리가 가는 곳은 관광지라고 하기 애매한 곳인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_4", /*message*/"　목적지는 니지가사키 학원! 당신의 안내를 받으며 교내를 둘러보는 마리. 그곳에서 니지가사키의 스쿨 아이돌, 아유무를 만나게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_5", /*message*/"　슬슬 누마즈로 돌아가겠다고 하는 마리에게 당신은 오늘 도쿄를 방문한 진짜 목적을 묻는다. 마리의 대답은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_6", /*message*/"　라이브 광고지를 돌리는 당신 앞에 나타난 마리. 또 도쿄 관광, 아니 학교 견학을 하러 온 모양이다. 하지만 이번에는 그것뿐만이 아닌 듯한데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_7", /*message*/"　다양한 학교를 방문하며 스쿨 아이돌들을 취재한 당신과 마리. 각자 사정이 있고 처한 상황이 다르다는 걸 알게 된 당신은 스쿨 아이돌 활동을 이어간다는 것의 무게를 다시금 실감한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_8", /*message*/"　마리의 진짜 목적은 Aqours의 앞날을 위해 정보를 수집하는 것이었다. 마리와 당신의 비밀스러운 도쿄 관광은 마지막 목적지로 접어든다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_1", /*message*/"　어느 날, 우라 여고 부실에 물건을 놓고 온 당신. 서둘러 부실로 돌아간 당신은 노을이 비치는 부실에서 혼자 작업하는 루비를 보게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_2", /*message*/"　루비를 도와 의상 수선을 마친 당신. 드디어 완성한 것 같은데, 루비는 여전히 부족하다고 느끼는 것 같다. 무엇이 그렇게 신경 쓰이는 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_3", /*message*/"　브로치의 재료를 모으기 위해 누마즈 밖에 있는 수예점까지 걸음을 옮긴 당신과 루비. 사고 싶은 재료를 찾을 수 있을까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_4", /*message*/"　브로치 만들기 스타트! Aqours의 멤버들에게 어울리는 것을 만들기 위해 루비는 뚜렷한 이미지를 잡아두고 있었다. 당신은 Aqours에 대한 루비의 애정이 얼마나 깊은지 다시금 실감한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_5", /*message*/"　드디어 브로치 완성! 눈앞에서 반짝이는 오리지널 액세서리에 감동하는 당신과 루비. 그러나 당신은 한 가지 이변을 눈치챈다. 대체 무슨 일이 벌어진 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_6", /*message*/"　Aqours 부실을 방문하자, 마침 치카와 요시코가 루비에게 액세서리 제작을 배우고 있었다. 브로치를 만드는 과정에서 루비의 신뢰도가 더 높아진 것 같아 루비도 기쁜 모양이다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_7", /*message*/"　루비와 함께 거리에 나선 당신. 카페에서 나와 걷던 중, 한 여성이 말을 걸어왔다. 그녀는 루비를 찾고 있던 모양인데, 대체 누구일까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_8", /*message*/"　루비가 잡지 스카우트를 받은 소식을 듣고 기뻐하는 멤버들. 하지만 당신의 표정은 밝지 않다. 아무래도 루비 본인이 별로 적극적이지 않은 모양인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_1", /*message*/"　μ&apos;s의 활동을 돕게 된 당신. 동료로 받아 줄지 걱정스럽기만 한데, 부실에서 기다리던 호노카는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_2", /*message*/"　&quot;같은 방향으로 함께 달려가는 동료&quot;. 호노카는 그렇게 말하며 당신을 맞이했다. 오늘도 당신은 오토노키자카 고교를 향해 가는…데 길을 잃고 말았다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_3", /*message*/"　길을 잃은 당신이 도착한 곳은 호노카의 집이었다. 항상 다른 멤버들을 활기차게 이끌어가는 호노카. 하지만 그런 그녀에게도 의외의 일면이 있는 것 같은데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_4", /*message*/"　호노카는 자기가 좋아하는 스쿨 아이돌에 관한 것이 아니면 의욕이 안 나는 듯하다. 하지만 그런 모습도 호노카답다는 생각이 드는데…. 호노카가 무엇을 좋아하는지 알게 된 당신은 한 가지 제안을 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_5", /*message*/"　부활동 외에서도 가까워져서 기쁜 호노카와 당신. 당신이 작곡할 때도 호노카가 손을 내밀어 주는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_6", /*message*/"　함께 손을 맞잡고 나아가는 두 사람. 무엇보다 서로 도움을 주고받을 수 있다는 게 기쁘다. 그리고 당신이 오기만을 손꼽아 기다리는 호노카는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_7", /*message*/"　유키호가 별생각 없이 던진 말을 의식한 호노카는 묘하게 안절부절못하는 모습이다. 그러면서도 니지가사키의 솔로 라이브를 보고 자극을 받은 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_8", /*message*/"　μ&apos;s의 솔로 라이브를 제안한 당신은 호노카와 둘이서 어떤 라이브를 할지 상의했다. 솔로 라이브를 향한 꿈은 점점 커지고… 잠깐, 너무 커졌나?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_1", /*message*/"　졸음을 참으며 아침을 먹는 당신의 집에 소꿉친구 아유무가 찾아왔다. 당신은 아유무와 함께 학교로 향한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_10", /*message*/"　동호회 멤버의 다음 라이브 소식을 알리려고 전단을 돌리는 아유무. 함께 전단을 돌려준 팬클럽 멤버가 아유무에게 이야기한 진실은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_11", /*message*/"　전단 돌리는 것을 도와준 팬클럽 사람들에게 뭔가 보답을 하고 싶어 하는 아유무. 감사의 마음을 어떻게 전하면 좋을지 당신에게 의견을 묻는다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_12", /*message*/"　팬클럽 사람들에게 감사의 마음을 전하기 위한 라이브 전날 밤. 라이브 준비를 마친 아유무가 당신을 위해 준비한 깜짝 이벤트가 있다는데?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_13", /*message*/"　지금까지 응원해 주는 팬들에게 고마운 마음을 전하는 라이브! 그런 기념비적인 라이브를 마친 아유무의 가슴속에는 어떤 생각이 싹트는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_14", /*message*/"　무사히 솔로 라이브를 마친 아유무는 스쿨 아이돌로서 더욱 성장하고 싶다며 당신과 이야기를 나눴다. 하지만 그 후, 당신이 감기에 걸리고 마는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_15", /*message*/"　아유무의 간호를 받고 기운을 차린 당신. 그런데 이번에는 아유무가…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_2", /*message*/"　점심시간이 되어 식당으로 이동한 당신과 아유무. 아유무는 직접 싼 도시락을 가져왔는데, 당신은 그 도시락에 담긴 정성과 색색의 반찬을 보고 감탄한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_3", /*message*/"　집에 가는 전철에서 당신은 아유무와 자신이 같은 부에 들어간 건 이번이 처음이란 걸 깨닫는다. 이를 기뻐하는 당신에게 아유무도 기쁜 표정으로….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_4", /*message*/"　어느 날 방과 후, 당신은 부실에서 활동 내용을 기록하고 있었다. 평소에 어떤 내용을 적느냐고 묻는 아유무에게 당신은 적은 내용을 읽어준다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_5", /*message*/"　라이브 이벤트 개최일. 니지가사키 멤버 중 아유무는 이번이 첫 라이브다. 좋은 결과가 나오길 기도하며 당신은 무대에 오르는 멤버들을 배웅한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_6", /*message*/"　솔로 이벤트를 어떻게 기획할지 고민하는 아유무. 자신의 부족함을 알고 있지만, 그런 자신의 성장을 함께 지켜봐 주기를 바라는 마음을 솔로 이벤트에서 표현하고자 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_7", /*message*/"　첫 솔로 라이브를 마치고 부실에서 라이브 영상을 확인하던 당신. 그때 아유무가 당신에게 영상 확인하는 걸 도와주겠다고 제안하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_8", /*message*/"　어느 날 밤, 학교 숙제를 하다 한숨 돌리려고 베란다에 나온 당신. 마침 아유무도 베란다에 나와 얼굴을 마주치게 되는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_9", /*message*/"　점점 더 인기가 많아지는 동호회 멤버들… 아유무는 그런 멤버들의 솔로 이벤트 설문 조사 결과를 확인하고, 인기가 늘어 바빠지는 멤버들과 비교해 자신이 뒤처지는 것 같다고 생각하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_1", /*message*/"　1학년 카스미는 다른 멤버들에게 자주 놀림 받는 편이다. 당신은 화를 내는 카스미를 사랑받는 증거라며 달랜다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_10", /*message*/"　동호회의 첫 공식 팬클럽인 카스미 팬클럽의 홈페이지를 만들게 된 카스미와 당신. 과연 무사히 홈페이지를 선보일 수 있을까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_11", /*message*/"　팬클럽의 홈페이지가 무사히 공개되고 가입자 수도 점차 늘어난다. 아유무와 아이의 팬클럽을 뛰어넘을 방법을 궁리하던 카스미는 팬클럽의 장점을 전파하기 위한 어떤 아이디어를 떠올린다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_12", /*message*/"　팬클럽 활동의 즐거움을 노래로 만들어 다음 라이브에서 선보이기로 한 카스미. 곡을 다 만들었는데도 불구하고 카스미는 어딘지 불안해 보이는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_13", /*message*/"　라이브 후, 팬들에게 받은 메시지를 기쁜 마음으로 읽는 카스미. 그 모습을 보고 자신의 힘이 부족하다고 느낀 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_14", /*message*/"　카스밍 팬클럽의 회원 번호 001번 회원으로 임명된 당신. 그런 당신에게 카스미가 상의할 일이 있다고 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_15", /*message*/"　카스밍 팬클럽의 쿠페빵 기획은 대성공! 하지만 카스밍은 &apos;카스밍 팬클럽&apos;의 치명적인 결점을 깨닫고 마는데…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_2", /*message*/"　커다란 가방을 끌어안고 부실에 나타난 카스미. 가방에는 대체 무엇이 들어 있을까? 카스미는 신나는 표정으로 가방 안에 들어 있던 것을 책상에 늘어놓는다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_3", /*message*/"　주말에 학교에서 열리는 오픈 캠퍼스에서 부활동 소개에 스쿨 아이돌 동호회도 참가하게 되었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_4", /*message*/"　임시 센터 투표 마감일. 자신의 장점인 귀여움과 치밀한 전략을 믿고 자신만만한 카스미. 센터로 뽑히는 사람은 과연 누구일까…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_5", /*message*/"　부활동 소개를 무사히 마친 동호회 멤버들. 카스미도 멤버들과 평소처럼 대화를 나누고 있지만, 속마음은 복잡한데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_6", /*message*/"　솔로 이벤트에서 자신을 응원해주는 사람들만이 느낄 수 있는 특별한 무언가를 준비하고자 하는 카스미. 더 친밀감을 주고 두근거리게 하기 위해 자기의 어떤 귀여운 점을 어필하면 좋을지 묻는데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_7", /*message*/"　솔로 이벤트의 여운에 젖어 있는 카스미. 하지만 한편으로는 팬들에게 자신의 귀여움과 매력을 더 확실하게 전하기 위한 다음 작전을 생각하고 있는 모양이다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_8", /*message*/"　한층 더 팬들의 마음을 사로잡기 위해 자신의 매력을 보여주고 싶어 하는 카스미! 카스미가 생각해낸 좋은 방법이란 과연 무엇일까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_9", /*message*/"　팬클럽을 만들기로 한 카스미는 아유무와 아이에게 이미 팬클럽이 있다는 사실을 알고 경쟁심에 불이 붙는데…! 카스미의 팬클럽은 대체 어떤 팬클럽이 될까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_1", /*message*/"　부활동 중 휴식을 취하며 시즈쿠와 이야기를 나누게 된 당신. 연극부 활동도 겸하는 시즈쿠는 언제 무대 연습이 잡혀도 문제없도록 매일 연기 연습을 하고 있다는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_10", /*message*/"　μ&apos;s와 Aqours를 만나 많은 것을 배우고 느낀 시즈쿠. 대략적인 방향은 정해졌지만, 아직 구체적인 방법이 떠오르지 않는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_11", /*message*/"　니지가사키 학원 스쿨 아이돌 동호회 멤버들을 연기하면서 자신다운 스쿨 아이돌의 모습을 찾아내기로 한 시즈쿠. 마침내 마음을 정한 시즈쿠는 자신이 목표로 하는 스쿨 아이돌의 모습을 당신에게 이야기한다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_12", /*message*/"　완벽한 몰입을 통해 다른 사람을 연기해 보겠다는 결심 끝에 만들어진 캐릭터! 솔로 이벤트가 며칠 앞으로 다가온 시즈쿠는 캐릭터를 완벽하게 연기하기 위한 특훈에 몰두하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_13", /*message*/"　함께 만들어낸 캐릭터를 훌륭하게 연기해 내고 이벤트를 마친 시즈쿠. 당신 앞에서 시즈쿠가 보여준 모습에는 이미 예전 같은 불안함은 없었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_14", /*message*/"　시즈쿠의 솔로 이벤트도 무사히 성공했다. 그러던 어느 날, 기쁜 얼굴로 웃고 있는 시즈쿠. 그 이유는…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_15", /*message*/"　시즈쿠가 평소의 고마움을 담아 당신을 에스코트해서 놀러 가고 싶은 곳이 있다고 한다. 과연 어떤 하루가 될까…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_2", /*message*/"　연극을 좋아하는 시즈쿠는 직접 연극 설정을 상상하는 것도 좋아한다고. 그 능력은 어렸을 때부터 이미 발휘되고 있었던 모양인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_3", /*message*/"　어느 날, 당신은 시즈쿠에게 연기 연습 상대가 되어 달라는 부탁을 받는다. 아무래도 대사 중에 막히는 부분이 있는 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_4", /*message*/"　두고 온 물건을 찾으러 부실을 찾은 당신은 안에서 기묘한 동작을 하는 시즈쿠를 만난다. 시즈쿠에게 무슨 일이 있었던 것일까?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_5", /*message*/"　시즈쿠를 위해 동호회 멤버들과 함께 시즈쿠의 배구 연습을 봐 주게 되었다. 시즈쿠는 약점인 구기 종목을 극복할 수 있을까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_6", /*message*/"　솔로 이벤트의 응원 메시지가 많지 않아 고민하는 시즈쿠. 완벽한 스쿨 아이돌이 되기 위해 더 열심히 하겠다고 결심하지만, 진정한 해결책은 그게 아니라….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_7", /*message*/"　솔로 이벤트를 끝낸 시즈쿠는 당신에게 이벤트에 대한 감상이 어떠한지 묻는다. 시즈쿠는 감상을 듣고 안심하면서도 뭔가 계속 마음에 걸린다는 듯한 눈치인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_8", /*message*/"　&quot;나다운 스쿨 아이돌이라는 게 뭘까요…?&quot; 그 문제에 대한 답을 얻기 위해 시즈쿠는 누마즈에 가서 Aqours 멤버들에게 질문을 던진다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_9", /*message*/"　Aqours 멤버들의 이야기를 들은 시즈쿠는 또 하나의 대답을 듣기 위해 호노카에게 간다. 거기서 μ&apos;s 멤버들이 주고받는 이야기는 시즈쿠에게 정말 신선하게 느껴지는 내용이었다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_1", /*message*/"　항상 노력을 게을리하지 않는 스스로에게 엄격한 카린. 어느 날, 부실로 돌아온 당신은 남아서 연습하는 카린을 발견한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_10", /*message*/"　아무래도 카린이 이상하다고 느낀 당신은 연습 중인 카린에게 말을 건다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_11", /*message*/"　걱정된 당신은 카린에게 말을 걸지만, 카린은 앞으로도 계속 지켜봐 달라며 말을 돌린다. 그런 상황 속에서 다음 라이브 준비가 시작되는데, 어떤 퍼포먼스를 선보여야 할까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_12", /*message*/"　지금까지 카린이 갖고 있던 이미지와는 다른 데모 곡을 만들어 온 당신. 카린도 관심을 가지고 그 곡을 다음 라이브에서 선보이기로 한다. 라이브 전날, 카린은 당신에게 계속 신경 쓰이던 것을 물어보는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_13", /*message*/"　주위의 기대에 부응하려고 노력하는 카린을 위해 당신이 만든 곡은 &apos;좀 더 의지해도 괜찮아, 솔직해져도 돼&apos;라는 메시지를 담은 곡이었다. 평소와 다른 분위기의 곡을 라이브에서 선보인 카린은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_14", /*message*/"　라이브를 무사히 마친 어느 날 거리에서 카린을 보고 말을 거는 당신. 카린은 어디로 가려는 걸까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_15", /*message*/"　카린과 동물원 데이트를 하게 된 당신. 동물원에 가는 목적은 바로… 판다!!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_2", /*message*/"　카린이 당신을 찾고 있었다며 말을 걸었다. 그 이유는 바로 데이트 신청…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_3", /*message*/"　매일같이 방과 후에 남아 연습하는 카린을 걱정하는 당신. 하지만 카린의 마음을 듣고 나서는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_4", /*message*/"　스쿨 아이돌을 시작한 뒤로 하루하루가 보람차다고 말하는 카린을 보고, 당신은 더 열심히 서포트할 것을 결심한다. 그러던 어느 날, 카린이 퍼포먼스에 대해 상의할 게 있다는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_5", /*message*/"　자신의 장점을 살리려면 어떻게 해야 할지 고민하던 카린은, 동호회 멤버들에게 의견을 듣고 한 걸음 더 앞으로 나아갔다. 하지만 연습을 거듭하던 중 살짝 벽에 부딪힌 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_6", /*message*/"　SNS 팔로워 수가 좀처럼 늘어나지 않는 카린. 이대로는 솔로 이벤트에도 영향을 미칠 것 같다. 카린이 팔로워 수를 늘리기 위해 세운 작전은 무엇일까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_7", /*message*/"　솔로 이벤트가 끝난 후, 어느 날 부실에서 카린을 빤히 바라보는 카스미. 그런 카스미가 카린에게 한 질문이란…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_8", /*message*/"　카스미와의 대화를 계기로 자신의 개성에 대해 생각에 잠긴 카린. 그러던 중 동호회 멤버가 섹시한 퍼포먼스를 해 보고 싶다는 말을 꺼내고, 카린이 그 연습을 돕기로 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_9", /*message*/"　당신은 카린에게 &apos;너라면 다른 스쿨 아이돌보다 훨씬 섹시하게 춤출 수 있다&apos;고 말하는데, 카린의 표정이 어둔가 어둡다. 부활동이 끝나고 동호회 멤버들끼리 패밀리 레스토랑에 왔지만, 역시 카린이 평소와 다른 느낌이 드는데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_1", /*message*/"　명랑하고 누구와도 친하게 지내는 아이. 인기인 아이에게 오늘도 사람들이 끊임없이 말을 걸어오는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_10", /*message*/"　아이를 응원하는 &apos;아이 친구&apos;와 미사토를 위해 다음 이벤트에서는 아이가 가사를 쓰기로 한다. 처음 해보는 작사에 아이는 악전고투하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_11", /*message*/"　당신의 조언을 받고 아이는 솔직한 마음을 가사에 담기로 한다. 한편, 당신은 미사토가 당신을 만나고 싶어 한다는 말을 듣고 미사토네 집에 갔다가 무언가에 대해 의논하게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_12", /*message*/"　&apos;아이 친구 그룹&apos;의 관리자이기도 한 미사토가 아이를 위한 깜짝 이벤트를 기획한다는 것을 알게 된 당신. 한편, &apos;아이 친구&apos;들을 위한 깜짝 이벤트로 열심히 가사를 쓰던 아이는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_13", /*message*/"　&apos;아이 친구&apos;를 생각하며 작사한 곡을 이벤트에서 선보인 아이. 아이가 &apos;아이 친구&apos;를 위해 준비한 서프라이즈는 대성공! 그리고 아이 친구가 아이를 위해 준비한 서프라이즈는…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_14", /*message*/"　아이의 솔로 이벤트는 대성공이었다! 아이와 당신은 다른 스쿨 아이돌의 라이브를 보러 하라주쿠를 찾게 되는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_15", /*message*/"　&apos;천둥이 무섭다&apos;는 아이의 새로운 일면을 알게 된 당신. 또 스쿨 아이돌 라이브를 보러 가자고 했지만, 아이에게는 선약이 있는 모양이다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_2", /*message*/"　선후배를 불문하고 친구가 많은 아이. 그런 아이가 자기 친구들을 초대해 동호회를 홍보하는 노래방 파티를 연다는데?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_3", /*message*/"　아이 주변에는 사람이 자연스럽게 모여들어 항상 활기가 있다. 어느 날, 당신은 아이, 리나와 함께 점심을 먹게 되었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_4", /*message*/"　아이는 할머니 손에 자랐고, 언니를 잘 따른다. 당신은 아이의 붙임성이 좋은 건 두 사람의 영향이 아닐까 생각하게 된다. 그 언니가 말했다는 &apos;와와 관계&apos;란…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_5", /*message*/"　스쿨 아이돌로서 사람들에게 웃음을 주고 싶다고 생각하게 된 아이. 그리고 찾아온 아이의 라이브 당일, 퍼포먼스는 어떻게 됐을까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_6", /*message*/"　아이가 다른 사람들과 하고 싶은 것은 바로… 친구가 되는 것! 그리고 아이를 좋아하는 사람들도 서로 친구가 되는 것! 그 꿈을 이루기 위해 생각해낸 작전이란?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_7", /*message*/"　솔로 이벤트를 무사히 끝낸 아이와 뒤풀이를 하기로 한 당신. 그 뒤풀이 장소는 바로…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_8", /*message*/"　아이네 몬자야키 가게에서 당신은 아이가 왜 그렇게 붙임성이 좋은지 알게 된다. 어느 날 아침, 아이가 부활동이 끝나면 뭘 할 건지 묻는다. 당신에게 소개하고 싶은 사람이 있다데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_9", /*message*/"　아이가 언니라고 부르며 따르는 미사토를 만나게 된 당신. 아이를 많이 도와달라는 미사토의 말에 새삼 마음을 다잡는다. 그렇게 대화를 나누던 중, 아이가 &apos;아이 친구 그룹&apos;이 생겼다며 기뻐한다. &apos;아이 친구&apos;란 대체 뭘까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_1", /*message*/"　평소처럼 부활동을 시작하려던 동호회 멤버들. 하지만 카나타가 보이지 않는다. 어쩔 수 없이 당신이 찾으러 갔더니, 카나타는 양호실에서 자고 있었다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_10", /*message*/"　하루카가 카나타를 멀리하던 이유를 듣게 된 당신은 카나타를 옥상으로 불러 하루카가 그런 행동을 했던 이유를 알려주기로 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_11", /*message*/"　연습 후에도 매일 남아서 노력하는 카나타. 하루카의 마음을 알게 된 후 의욕이 넘친다. 그런 와중에 카나타는 어떤 결심을 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_12", /*message*/"　라이브 전날에 드디어 마음을 가득 담은 곡을 완성한 카나타. 모두가 기뻐해 줄지 불안해하지만, 당신의 응원에 자신감을 얻는다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_13", /*message*/"　라이브는 대성공! 고집을 부리던 자신을 이해해 줘서 안도하는 카나타. 바로 그때 하루카에게서 메시지가 온다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_14", /*message*/"　솔로 이벤트를 성황리에 마치고, 카나타와 하루카도 무사히 화해했다! 이벤트가 끝난 어느 날, 카나타로부터 놀러 가자는 전화가 온다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_15", /*message*/"　카나타, 하루카와 함께 우치우라에 낚시를 하러 가게 된 당신. 치카와 카난도 합류하는데…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_2", /*message*/"　부활동에 늦지 말라고 카나타에게 당부하고자 카나타의 반을 찾아간 당신. 그러나 카나타의 모습이 보이지 않는다. 어디로 간 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_3", /*message*/"　부활동 중, 열심히 작사를 하는 당신에게 카나타가 다가왔다. 동생 하루카를 정말 좋아하는 카나타는 당신에게 자기가 동생을 얼마나 사랑하는지 말한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_4", /*message*/"　점심시간에 학교 식당에서 카나타가 당신을 찾아왔다. 도시락을 너무 많이 싸 왔으니 먹어 달라고 하는데.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_5", /*message*/"　부활동을 마치고 깊이 잠들어 버린 카나타. 일어날 기미가 없자 동호회 멤버들이 걱정하기 시작한다. 당신은 예전에 카나타가 말했던 &apos;졸린 이유&apos;를 떠올리고….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_6", /*message*/"　솔로 이벤트에서 팬들에게 뭔가를 부탁하고 싶어서 가슴이 콩닥거리는 카나타. 카나타가 하려는 부탁이란…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_7", /*message*/"　솔로 이벤트를 무사히 끝내고 꿈결 같은 기분에 젖어 있는 카나타. 팬들을 더 기쁘게 하기 위해 어떻게 하면 좋을지 고민하는 카나타를 보고 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_8", /*message*/"　날씨가 좋으니 중앙 정원에서 점심을 먹기로 한 아유무와 당신. 그때 심상치 않은 기세로 카나타가 달려온다. 무슨 일이 있는지 묻자 그녀가 놀라운 이야기를 들려주는데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_9", /*message*/"　하루카에게 미움받았다며 속상해하는 카나타는 하루카 생각에 며칠 동안이나 밤잠을 이루지 못한다. 부실에서 잠들어 버린 카나타를 위해 남아 있던 당신은 돌아가는 길에 카나타에게 하루카와 어떻게 되어가고 있는지 묻는다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_1", /*message*/"　스쿨 아이돌 &apos;유키 세츠나&apos;, 그리고 학생회장 &apos;나카가와 나나&apos;로서 하루하루를 보내는 세츠나. 그런 세츠나를 바라보는 당신은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_10", /*message*/"　당신의 집에 신세를 지게 된 세츠나. 당신은 세츠나에게 지금까지 입 밖으로 내지 못했던 질문을 한다. 그 질문은 바로 세츠나가 가출을 한 원인이 된 부모님과 나눈 대화에 대해서였다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_11", /*message*/"　당신의 조언을 듣고 세츠나는 자신이 스쿨 아이돌 활동을 &apos;얼마나 사랑하는지&apos; 부모님께 털어놓기로 한다. 막상 결심은 했지만, 실제로 부모님께 전화하려니 역시 불안한 모양인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_12", /*message*/"　스쿨 아이돌 활동에 대한 자신의 &apos;사랑&apos;을 이해해 주셨으면 하는 마음에 부모님께 연락해 무대를 보러 와 달라고 부탁한 세츠나. 그러나 라이브 전날 세츠나는 갑자기 부실에서 모습을 감추고, 당신은 세츠나를 찾아간다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_13", /*message*/"　응원해 주는 사람들, 그리고 부모님을 향해 자신의 &apos;사랑&apos;을 전하는 퍼포먼스를 힘껏 펼친 세츠나. 그 무대를 본 세츠나의 부모님은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_14", /*message*/"　이벤트는 대성공을 거두고 세츠나의 부모님도 스쿨 아이돌 활동을 허락해 주셨다!
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_15", /*message*/"　세츠나, 리코와 함께 좋아하는 것에 대해 이야기꽃을 피운 후 어느 날, 부실에서 보드게임을 즐기던 당신. 세츠나의 &apos;사랑&apos;은 게임에서도 예외가 아니었다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_2", /*message*/"　&apos;세츠나&apos;로서도 &apos;나나&apos;로서도 열심히 노력하는 세츠나를 존경하는 당신. 하지만 가끔 힘들 때도 있는 것 같다. 어느 날, 세츠나로부터 부활동을 쉬겠다는 연락이 왔다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_3", /*message*/"　세츠나는 부모님 몰래 스쿨 아이돌 활동을 하고 있었다. 그런 세츠나가 스쿨 아이돌과 비슷한 정도로 &apos;좋아하는 것&apos;이 있다고 하는데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_4", /*message*/"　애니메이션과 만화를 좋아하는 세츠나. 그래서 당신은 세츠나가 읽고 싶은 만화책이 있는 집으로 세츠나를 초대하기로 했다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_5", /*message*/"　세츠나네 집에서는 애니매이션, 만화와 스쿨 아이돌까지도 금지이다. 그래서 &apos;유키 세츠나&apos;란 예명으로 활동했다고 한다. 스쿨 아이돌에 대한 세츠나의 뜨거운 열정을 알게 된 당신은 시즈쿠, 리나와 함께 학생회 일을 돕기로 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_6", /*message*/"　솔로 이벤트를 앞두고 자신을 가혹하게 몰아붙이는 세츠나. 그런 세츠나를 보고 당신은 조금 걱정이 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_7", /*message*/"　부모님과의 갈등으로 이벤트 당일에 지각하고 만 세츠나. 그 후에도 계속되는 불화에 결국 세츠나의 인내심이 바닥을 드러내는데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_8", /*message*/"　학생회장을 그만둔 후 처음으로 스쿨 아이돌 활동에 대해 부모님과 이야기를 나눈 세츠나. 스쿨 아이돌 활동을 이해해주지 않는 부모님에게 점점 화가 치밀어 오른 그녀는 결국 가출을 결심하는데! 가출 첫날, 세츠나가 향한 곳은 카린과 엠마의 기숙사 방이었다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_9", /*message*/"　아직도 가출 중인 세츠나. 전날 세츠나를 재워준 카스미가 당신에게 간곡하게 상담을 부탁하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_1", /*message*/"　스쿨 아이돌로서 매일 노력하고 있는 엠마. 그런 엠마가 스위스에서 일본으로 오게 된 이유는…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_10", /*message*/"　연습이 끝나고 엠마와 회의를 하는 당신. 다음 곡에 관한 이야기를 나누던 중, 엠마가 다음 곡에 대해 어떤 제안을 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_11", /*message*/"　엠마가 항상 노래하러 가는 길거리 무대에 따라간 당신. 평소처럼 엠마가 노래를 부르자 소년 한 명이 함께 노래해 주고….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_12", /*message*/"　라이브를 앞둔 어느 날, 당신은 부실에서 엠마가 한숨을 쉬는 모습을 목격한다. 평소답지 않게 한숨을 쉬는 엠마의 이야기를 들어 보니 조금 자신이 없는 듯한데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_13", /*message*/"　&quot;네 마음을 어루만져 주고 싶어&quot;…. 그렇게 말한 엠마의 라이브가 끝났다. 라이브에서 팬들의 따뜻한 마음을 느낀 엠마는 한 가지 결심을 하게 된다!!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_14", /*message*/"　어느 날 아침 일찍 부실로 향한 당신. 하지만 엠마가 먼저 와 있었다. 엠마는 아침부터 무엇을 하고 있었을까…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_15", /*message*/"　부실이 항상 깨끗한 것은 항상 이른 아침부터 청소해 주는 엠마 덕분이라는 사실을 알고 감사하는 당신.
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_2", /*message*/"　어렸을 때 동영상에서 본 스쿨 아이돌을 동경하여, 본인도 스쿨 아이돌이 되기 위해 일본으로 유학을 왔다고 하는 엠마. 엠마에게 스위스에서의 생활을 물어봤더니….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_3", /*message*/"　언젠가 스위스에서도 라이브를 하자고 약속한 엠마와 당신. 어느 날, 동호회 멤버들이 모여 각자 어떤 스쿨 아이돌이 되고 싶은지 이야기하게 되었다. 그때 엠마는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_4", /*message*/"　스쿨 아이돌 활동을 하면서 자기가 어떤 스쿨 아이돌이 되고 싶은지 탐색해 보기로 한 엠마. 그러던 어느 날, 엠마네 집에서 다음 이벤트를 위한 회의를 하게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_5", /*message*/"　엠마의 모습을 보고 다정하게 감싸 주는 스쿨 아이돌이 될 수 있을 것 같다고 생각한 당신은, 엠마에게 어떤 상의를 하기로 한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_6", /*message*/"　솔로 이벤트의 테마는 정해졌지만, 구체적인 내용을 떠올리지 못하는 엠마. 치유란 무엇일까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_7", /*message*/"　어느 날, 당신은 중앙 정원에서 꽃에 물을 주는 엠마와 만난다. 기숙사 후배 대신 꽃을 돌보는 엠마를 돕다가 예전에 있었던 솔로 이벤트의 기억을 떠올린다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_8", /*message*/"　꽃에 물을 준 답례로 기숙사 후배에게 쿠키를 받은 엠마. 부실에서 동호회 멤버들과 함께 쿠키를 먹다 보니 엠마가 쿠키를 받았을 때의 이야기가 화제에 오르는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_9", /*message*/"　엠마가 당신에게 &quot;일요일에 보육원으로 자원봉사를 하러 가자&quot;는 부탁을 한다. 평소 남에게 좀처럼 무언가를 부탁하는 일이 없는 엠마가 모처럼 한 부탁을 당신은 흔쾌히 받아들이는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_1", /*message*/"　당신과 아이가 이야기를 나누고 있을 때 리나가 찾아왔다. 보드의 표정을 바꾸기 전에 당신이 리나의 감정을 맞히자 리나는 놀라워한다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_10", /*message*/"　다른 사람들과 더 친밀한 사이가 되고, SNS에서도 더 많이 알려지기 위해 노력하자고 결심하는 리나. 어느 날 옥상에 불려 나온 당신에게 리나가 고민 상담을 해 온다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_11", /*message*/"　리나보드를 없애는 게 좋을지 고민하는 리나. 솔로 이벤트의 동영상을 올린 후, 팬들이 달아준 댓글을 본 리나의 결단은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_12", /*message*/"　다음 라이브에서 리나보드를 없애기로 한 리나는 라이브 전날, 당신에게 내일 라이브에 대한 굳은 결의를 보여준다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_13", /*message*/"　지금 리나가 할 수 있는 가장 멋진 무대를 보여주겠다며 단단히 마음먹고, 리나보드 없이 처음으로 맞이한 라이브. 그 결과는…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_14", /*message*/"　리나의 이벤트는 대성공. 멤버들이 변함없이 연습에 힘쓰던 어느 날, 리나가 약간 이상해 보인다. 요즘 부활동이 끝나자마자 귀가하는 리나… 방과 후에 뭘 하는 걸까?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_15", /*message*/"　리나는 방과 후 교내에 있는 아기 고양이를 보살펴 주고 있었다. 이를 알게 된 동호회 멤버들은 다 같이 고양이를 돌보기로 했다. 그러던 어느 날, 리나가 황급히 부실로 뛰어 들어오는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_2", /*message*/"　리나 보드 덕분에 주변 사람과 연결점을 가지게 된 리나. 리나는 더 많은 사람과 이어지고 싶다고 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_3", /*message*/"　리나를 데리고 스쿨 아이돌 라이브를 보러 온 당신. 수많은 사람이 스쿨 아이돌을 보기 위해 모인 광경을 보고 리나는 크게 놀라는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_4", /*message*/"　라이브에서 수많은 사람과 한마음이 되기 위해 매일 연습에 힘쓰는 리나. 그러나 라이브를 하는 데 중대한 문제가 있다는 것이 드러났다. 그것은 바로….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_5", /*message*/"　리나의 첫 라이브 날. 리나는 &apos;새로운 아이템&apos;과 함께 무대에 오른다. 공연장에 온 사람들과 한마음이 되기를 바라면서….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_6", /*message*/"　스쿨 아이돌을 시작하면서 다른 사람의 주목을 받게 된 리나는 다른 사람과 더욱 마음을 나누고 싶어한 나머지, 전교생을 라이브에 초대하려 하는데!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_7", /*message*/"　솔로 이벤트가 끝난 후, 정신이 딴 곳에 가 있는 리나가 걱정된 카스미와 시즈쿠가 당신에게 도움을 요청해 왔다. 당신은 리나와 직접 이야기를 나눠 보는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_8", /*message*/"　솔로 이벤트의 여운에 젖어 있던 리나. 앞으로도 응원해주는 사람들과의 인연을 소중히 여기겠다고 결심한다. 그래서 리나는 아이에게 SNS에 올릴 내용에 관해 의견을 구하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_9", /*message*/"　당신과 아이와 함께 처음으로 찍은 셀카 사진을 보며 기뻐하는 리나. 셀카 사진을 올린 후 SNS 방문자도 늘어났다. 그리고 SNS가 주목을 받으면서 TV에서도 리나에 대해 다루는데…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_1", /*message*/"　노래와 춤만으로는 무대가 성립되지 않는다! 그 뒤에서는….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_2", /*message*/"　오토노키자카 고교의 학생회장이기도 한 에리. 당신은 학생회를 견학하게 되는데… 잠깐, 이건 견학이 아니잖아?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_3", /*message*/"　에리 덕분에 부활동 지원도 원활하게 풀리고 있다. 한숨 돌린 당신은 어떤 소리를 듣게 되는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_4", /*message*/"　한밤중의 학교에 울려 퍼진 에리의 비명. 소리도 없이 다가온 그것은 공포인가, 아니면….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_5", /*message*/"　에리의 매력을 체감한 덕에 부활동의 보람이 날마다 커져 가네요♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_6", /*message*/"　당신의 도움을 받아 알찬 나날을 보내는 에리. 동생인 아리사도 당신의 존재가 궁금한 듯한데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_7", /*message*/"　아리사가 무심코 던진 한마디 때문에 당신을 의식하게 된 에리. 솔로 라이브를 향해 움직이기 시작할 때도 당신의 도움을 든든하게 느끼고….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_8", /*message*/"　에리는 μ&apos;s 솔로 라이브를 하기로 결정했지만, 승인을 받아야 할 이사장님과 연락이 되지 않는다. 이대로 진행해도 될지 불안한 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_1", /*message*/"　μ&apos;s의 활동을 돕게 된 당신은 스쿨 아이돌이 되기 전부터 친하게 지냈던 호노카와 우미, 코토리 세 사람의 이야기를 듣게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_2", /*message*/"　평소에는 호노카와 우미를 다정하게 도와주는 코토리. 하지만 코토리에게도 절대 타협할 수 없는 무언가가 있는 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_3", /*message*/"　μ&apos;s의 의상 제작을 담당하고 있는 코토리. 코토리의 완벽주의를 알게 된 당신은 거리에서 코토리와 우연히 마주치는데… 잠깐, 코토리가 아니라고?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_4", /*message*/"　메이드 카페에서 미나린스키라는 이름으로 아르바이트를 하던 코토리. μ&apos;s와 메이드 일 모두 최선을 다하고 있지만, 의상 때문에 고민이 있는 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_5", /*message*/"　의상 때문에 고민하던 코토리를 데리고 박물관을 찾은 당신. 코토리는 어려울 때 도움을 주는 당신의 존재를 든든하게 여기는 것 같다…♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_6", /*message*/"　코토리는 당신에게 도움을 받았던 일, 당신과 놀러 갔던 일을 매우 기뻤던 모양이다. 화제도 자연스럽게 당신의 이야기로 옮겨가는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_7", /*message*/"　호노카와 우미가 소중한 것처럼, 당신과 이야기하는 시간도 소중하다고 생각하는 코토리. μ&apos;s의 솔로 라이브를 하기 전에 코토리는 확인하고 싶은 게 있었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_8", /*message*/"　코토리는 솔로 라이브가 기대된다고 하지만, 자기 라이브보다 다른 멤버들의 라이브가 더 기대되는 모양이다. 어떤 라이브가 좋을지 코토리가 당신에게 의견을 물어보는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_1", /*message*/"　μ&apos;s를 돕게 된 당신은 우미가 스쿨 아이돌에 대해 어떻게 생각하는지 듣게 된다. 우미에게 스쿨 아이돌이란?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_2", /*message*/"　스쿨 아이돌. 그것은 호노카의 꿈이자 우미의 꿈이기도 했다. μ&apos;s의 곡은 대부분 우미가 작사하고 있다는 이야기를 들은 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_3", /*message*/"　우미의 집에서 작사하는 모습을 견학하게 된 당신. 우미가 마음을 담아 작사하고 있을 때, 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_4", /*message*/"　우미가 풍부한 감성으로 작사하는 모습을 보고, 당신은 자신이 우미에게 점차 끌리고 있다는 걸 느낀다. 그러던 어느 날, 우미가 출전하는 궁도 대회에 따라가게 되는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_5", /*message*/"　μ&apos;s의 활동 외에서도 나타나는 우미의 다양한 매력. 앞으로도 서로에 대해 더 알아가고 싶다고 느낀 두 사람은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_6", /*message*/"　μ&apos;s 활동을 착실하게 도우면서 우미와의 교류도 나날이 깊어지는 당신. 우미는 당신에게 고마운 마음을 표현하고 싶어 하는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_7", /*message*/"　우미는 당신에게 감사의 마음을 담은 편지를 보낸다. 열정 넘치는 편지를 받은 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_8", /*message*/"　우미와 함께 솔로 라이브 준비를 하게 된 당신은 생각지도 못했던 우미의 약점을 알게 되는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_1", /*message*/"　언제나 기운 넘치는 린. 마키와 하나요와 함께 인사하자♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_2", /*message*/"　하나요와 늘 사이좋은 린. 당신은 뭔가 도움이 필요하진 않은지 물어보는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_3", /*message*/"　하나요를 대신해 린을 응원하게 된 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_4", /*message*/"　하나요의 주먹밥을 먹고 기뻐하는 린. 하지만 어쩐지 쑥스러워하는 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_5", /*message*/"　당신의 응원을 받고 라면 맛도 모를 정도로 기뻐한 린은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_6", /*message*/"　당신의 서포트를 기대하게 된 린. 부활동을 엄청 열심히 하면 같이 놀러 갈 수 있을지 생각하며 이것저것 계획 중!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_7", /*message*/"　당신과 함께하는 부활동과 나들이를 기대하는 린. 하지만 자신의 솔로 라이브라는 말을 듣자, 조금 다른 반응을 보이는 듯한데…?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_8", /*message*/"　솔로 라이브 준비를 시작하자고 제안하는 당신에게 린이 한 말은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_1", /*message*/"　μ&apos;s의 활동을 돕게 된 당신은 μ&apos;s에서 작곡을 담당하고 있는 마키와 대화하게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_2", /*message*/"　당신에게 도움을 받을 필요를 못 느끼겠다는 마키. 다만 당신을 거부하는 건 아닌 것 같은데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_3", /*message*/"　마키가 앞으로 친해지고 싶다고 말해 주었다. 인간관계는 서툴어도 다정한 사람이다. 그런 그녀와 당신이 음악실에서 만나….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_4", /*message*/"　피아노를 치면서 서로에 관해 이야기를 나눈 두 사람. 피아노를 치는 이유도 왠지 공감이 된다. 마침 당신이 작곡하고 있을 때, 마키가 지나가다가….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_5", /*message*/"　작곡에 재능이 있다고 마키에게 칭찬받은 당신. μ’s의 곡과 마키의 한마디가 큰 힘이 된다는 걸 실감하고…♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_6", /*message*/"　너와 함께라면 새로운 μ&apos;s를 만들 수 있을 거야... 라며 당신을 맞이해준 마키. 자기 소개 영상을 촬영할 때도 심경의 변화가 나타나는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_7", /*message*/"　자꾸 당신 생각을 하게 된다며 못마땅한 티를 내는 마키. 솔로 라이브 제안에도 솔직하게 대답하지 못하는 것 같은데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_8", /*message*/"　마키는 평소에 하던 라이브와 내용이 같으면 솔로 라이브를 할 이유가 없다고 한다. 당신과 마키는 솔로로 활동하는 니지가사키 멤버들을 찾아가 이야기를 들어 봤지만, 마키는 라이브보다 더 신경 쓰이는 게 있는 듯했다….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_1", /*message*/"　재미있는 사람이 왔다며 기뻐하는 노조미. 하지만 그 뜻은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_2", /*message*/"　학생회에서 부회장으로 활동하는 노조미. 노조미가 학교생활에 대해 어떤 생각을 품고 있는지 알게 된다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_3", /*message*/"　노조미의 특기인 점술에서… 뜻밖의 점괘가!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_4", /*message*/"　좋지 않은 점괘가 나왔다. 노조미의 조언을 받아 이 위기에서 벗어나고 싶어!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_5", /*message*/"　낙제점을 받지 않기 위해, 노조미의 조언에 따르기로 한 당신은…");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_6", /*message*/"　노조미 덕분에 자연스럽게 모두와 친해진 당신. 오늘도 노조미는 부실에서 당신을 기다리는 것 같아요♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_7", /*message*/"　노조미는 당신이 자주 부실에 와 주는 게 기쁜 모양이다. 하지만 자신의 솔로 라이브 얘기가 나오자, 조금 반응이 달라지는 듯한데…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_8", /*message*/"　μ&apos;s 솔로 라이브 개최가 결정되자, 지금까지와는 다른 라이브가 될 것 같다며 기뻐하는 노조미. 당신은 당연히 노조미의 라이브 준비를 도울 생각이었지만, 노조미가 보인 반응은…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_1", /*message*/"　하나요가 무엇보다도 좋아하는 것, 그것은 물론…!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_2", /*message*/"　매사에 소극적이던 하나요의 손을 잡고 끌어준 것은 린이었다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_3", /*message*/"　아침에 아키하바라를 거닐던 당신은 하나요를 발견한다. 그런데 하나요의 모습이 평소와 다른데?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_4", /*message*/"　오늘은 하나요가 왠지 즐거워 보인다. 그 이유는…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_5", /*message*/"　함께 돌아다니며 하나요와 거리가 점점 가까워졌다.");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_6", /*message*/"　조금씩 깊어지는 당신과 하나요의 인연. 함께 아침 연습을 하기로 마음을 새롭게 다잡는 하나요에게도 걱정이 하나 있는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_7", /*message*/"　니코의 도움을 받아 아침 연습을 위한 연습을 시작한 하나요. 그 연습이 솔로 라이브에서도 도움이 될지도…?");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_8", /*message*/"　하나요는 당신과 함께라면 솔로 라이브도 문제없다며 의욕을 보였지만, 혼자 무대에 서는 게 아무래도 불안한 모양이다. 그 불안을 해소해 주기 위해 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_1", /*message*/"　μ&apos;s의 부활동을 돕게 된 당신. 그것이 불만인 멤버가 딱 한 명 있는데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_2", /*message*/"　니코에게 스쿨 아이돌을 향한 자신의 마음이 진심이라는 걸 보여주고 싶은 당신. 니코는 의심을 거두지 못하는 눈치인데….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_3", /*message*/"　기대에 부응하려 노력하는 당신. 그 열의가 점차 니코에게도 전해지고….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_4", /*message*/"　스쿨 아이돌을 좋아하는데 왜 직접 스쿨 아이돌이 되지 않냐고 묻는 니코에게 당신은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_5", /*message*/"　니코에게 인정받고 싶은 당신. 특훈의 성과를 발휘할 때가 왔다!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_6", /*message*/"　당신을 함께 무대를 만들어갈 동료로 인정해 준 니코. 에리와 노조미도 기뻐하는 듯한데 니코 본인의 생각은….");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_7", /*message*/"　지금보다 더 좋은 무대를 만들기는 쉬운 일이 아니라며 마음을 다잡는 니코. 니코에게 인정받아 기쁜 당신도 마냥 좋아할 수만은 없을 것 같은데?!");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_8", /*message*/"　니코의 솔로 라이브를 우주 끝까지 전하자며 의욕이 넘치는 니코와 당신. 니코의 귀여운 퍼포먼스를 내세운 솔로 라이브를 할 줄 알았는데… 아무래도 니코는 생각이 다른 것 같다.");
