INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_42_5", /*message*/"　雑誌のスクールアイドル特集企画を引き受けることにしたμ&apos;s。今日はいよいよその当日で……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_42_6", /*message*/"　ファッション誌のスクールアイドル特集に出たμ&apos;s。写真撮影にインタビューでスクールアイドルの魅力を宣伝した。その反響は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_42_7", /*message*/"　雑誌の企画がきっかけで、テレビ局からクイズ番組出演のオファーを受けたμ&apos;s。これで優勝すれば世界をあっと驚かすことができる！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_42_8", /*message*/"　クイズ番組に出演した穂乃果たち。にこも穂乃果も真姫も死力を尽くした！　さて、その結果は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_42_9", /*message*/"　クイズ番組収録後の打ち上げで些細な言い合いになってしまった穂乃果とにこ。にこの言うことをちゃんと考えてみると言った穂乃果は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_1", /*message*/"　雑誌の企画、テレビ番組と立て続けに慌ただしい日々を過ごしたμ&apos;s。久しぶりにゆっくりした時間を取ることができて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_10", /*message*/"　これからのμ&apos;s、これからの目標を見据えた穂乃果たち。今日はきっかけをくれたAqoursへお礼を言いに……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_2", /*message*/"　世界を驚かせるため、スパルタ特訓を始めたμ&apos;s。ヘトヘトになった穂乃果が癒されに行ったところは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_3", /*message*/"　メイド喫茶で楽しんだ穂乃果と海未。アルバイトが終わったことりと連れ立って散策へ。久しぶりに歩くアキバは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_4", /*message*/"　よく知る街がいつのまにか変わってきていたことを寂しく感じる穂乃果。アキバはこのまま変わっていってしまうのか……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_5", /*message*/"　アキバでの突発ライブがきっかけで、にこの本当の思いがわかった穂乃果。一方にこは最近の微妙な空気を案じていて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_6", /*message*/"　変わらないものを作るために変わっていくことを選んだ穂乃果たち。もっともっと新しいチャレンジを見つけようと作戦会議！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_7", /*message*/"　μ&apos;sとして進化したい、できることをどんどん積み上げたい、と案を出し合った穂乃果たち。一方、その勢いに乗り切れない１年生は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_8", /*message*/"　２、３年生の勢いに乗り切れていない１年生たち。しかし穂乃果たちもそれはしっかり感じ取っていた。１年生に自分たちの想いを伝える曲を作ることを思いついた穂乃果たちは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_43_9", /*message*/"　伝えたい想いを込めた曲を作り上げた穂乃果たち。そのタイトルは穂乃果に託されて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_1", /*message*/"　WEB上でのファンクラブ活動を充実させている璃奈。それは璃奈ならではのファンとの繋がり方。あなたも夜遅くまでゲーム大会で盛り上がってしまい……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_10", /*message*/"　「応援は無意味」と言われたことが気になるあなた。だけどそれにばかり気を取られてはいられない。高校生ゲームクリエイターコンテストが明日に迫っている！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_2", /*message*/"　進路希望調査が全校生徒に配布された。まだ形にならない思いを抱えながら部室に向かうと、そこで璃奈からある相談があって……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_3", /*message*/"　璃奈の決意。それは「高校生ゲームクリエイターコンテスト」に出場すること。そしてそのメンバーに誘われたあなた。でもあなたは自分が戦力になれるかわからなくて返事を保留してしまい……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_4", /*message*/"　高校生ゲームクリエイターコンテストに出場を決めたあなた、璃奈、せつ菜、ミア。コンテストに向けて、まずはゲームのお勉強……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_5", /*message*/"　進路希望調査にどう書くか迷っている果林。書いては消してで思考の迷路に迷い込み……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_6", /*message*/"　高校生ゲームクリエイターコンテストに向けて、自主的にゲームの勉強をしているあなた。流行りのリサーチもしようと穂乃果たちに話を聞いて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_7", /*message*/"　高校生ゲームクリエイターコンテストの準備は着々と進行しているよう。そんな中、寮ではいつもの風景が。エマはみんなの世話をあれこれと焼いている。みんなの今日の予定を聞いてみると……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_8", /*message*/"　進路希望調査をきっかけに、それぞれが新しいチャレンジを始めているニジガクのメンバー。彼方も進路に関する悩みがあり……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_44_9", /*message*/"　ニジガクの定期ライブがやってきた！　今回はそれぞれやることがあり十分な練習時間が取れないメンバーもいたけれど、PV制作以降ニジガクのスクールアイドル熱は高まる一方！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_1", /*message*/"　「高校生ゲームクリエイターコンテスト」を翌日に控え、準備をするあなたたち。改めてコンテストのおさらいをしたり、買い出しに行ったりと、ちょっと遠くに遊びに行く前日のような高揚感に溢れている");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_10", /*message*/"　璃奈の新しい挑戦「高校生ゲームクリエイターコンテスト」。本当に作りたいものを諦めず挑戦し、ついに完成させた。そしてコンテストの結果は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_2", /*message*/"　校内フィルムフェスティバルでお手伝いをした各部からの応援もあり、万全の態勢でコンテストに臨むことになったあなた。ついに今日はコンテスト当日！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_3", /*message*/"　コンテストのテーマは「夢」。みんなにとっての「夢」は、スクールアイドルとして想いを込めた歌をたくさんの人に届けること。リズムゲームで夢を届けよう！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_4", /*message*/"　今日は外に夕食をとりに出たあなたたち。バーガーが食べたいというミアのために、いつもの場所に行ったら……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_5", /*message*/"　ゲーム作りの進捗は順調。想定よりいいスピードで仕上がっている。しかし、作業に没頭してる璃奈の様子がおかしいことに気付いたあなたは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_6", /*message*/"　本当は作ってみたいゲームがあった璃奈。璃奈の話を聞いて、あなたがしたことは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_7", /*message*/"　璃奈の作りたいもののイメージから、落ちものパズルを選択したあなたたち。急ピッチで作業を進めて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_8", /*message*/"　ラストスパートに向けて、交代で休憩をとることにしたあなた。これからのことを考えつつ仮眠室である部室に向かっていると、そこには……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_45_9", /*message*/"　短い夜が明け、ついにラストスパート。それぞれがそれぞれの役割をこなしていく。ゲーム完成なるか！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_1", /*message*/"　高校生ゲームクリエイターコンテストも無事終わり、あなたもホッとひと息。そんなとき、果林に撮影について来てほしいと相談を受ける。普段あまり頼まれないため不思議に思うも、一緒に付いて行くことに。その撮影とは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_10", /*message*/"　全国高校生No1パティシエ決定戦でグランプリに輝いた彼方とツムギの「応援マカロン」。今日はグランプリ受賞のお祝いパーティがあって……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_2", /*message*/"　将来を見据えての活動が活発になってきている同好会メンバー。それを見て決意を新たにする彼方。自分も全国高校生No1パティシエ決定戦に出て結果を出したいと、試作に励むのだった");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_3", /*message*/"　ツムギの話を聞いて、何も声をかけることができなかった彼方。彼方はそのことをずっと気にしていて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_4", /*message*/"　今日はちょっと遠出して合同ライブ！　そこにはスクールアイドル活動以外を見てニジガクを知ってくれた人たちも多くきていた。それを見たメンバーの気合いも十分！　ようこそ、ニジガクライブへ！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_5", /*message*/"　ライブ会場を後にしようとしたツムギをなんとか引き留めたあなた。そこであなたはツムギの過去を知ることに……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_6", /*message*/"　ツムギの過去を聞いたあなた。とても難しくて複雑な問題だ。だけど、どう考えても応援が悪いことだとは思えない。迷ったあなたは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_7", /*message*/"　ツムギに応援の気持ちを届けたい、そのためのライブをすると決めた彼方。準備は順調に進み、いよいよライブ開催！");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_8", /*message*/"　彼方のライブを観たツムギは、もう一度お菓子作りに挑戦すると決めた。そして、彼方と一緒に全国高校生No1パティシエ決定戦に出ることも。彼方の想いがしっかりと届いたことを見たあなたは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_46_9", /*message*/"　全国高校生No1パティシエ決定戦には、思い出のマカロンで勝負しようと決めた彼方とツムギ。いよいよ当日！　思い出のマカロンは再現できるのか！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_47_1", /*message*/"　進路希望調査をきっかけに、それぞれが新しいチャレンジを経験したメンバー。その熱に煽られて、同好会の練習にも熱が入っていて……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"story_main_summary_47_10", /*message*/"　歩夢との会話で、自分に欠けていた視点に気がついたあなた。早速翌朝、みんなが集まったときに相談をはじめて……？");
