CREATE TABLE m_story_event_history_detail(
  story_event_history_id INTEGER NOT NULL,
  story_event_id INTEGER NOT NULL,
  event_master_id INTEGER NOT NULL,
  story_number INTEGER NOT NULL,
  title TEXT NOT NULL,
  description TEXT NOT NULL,
  banner_thumbnail_path TEXT NOT NULL,
  detail_thumbnail_path TEXT NOT NULL,
  scenario_script_asset_path TEXT NOT NULL,
  story_unlock_item_id INTEGER NOT NULL,
  amount INTEGER NOT NULL,
  PRIMARY KEY (story_event_history_id, story_event_id, event_master_id),
  UNIQUE (story_event_id),
  FOREIGN KEY (story_event_history_id) REFERENCES m_story_event_history(id),
  FOREIGN KEY (story_unlock_item_id) REFERENCES m_story_event_unlock_item(id)
);
